import React, { Component } from 'react';
import { withRouter } from 'react-router';
import Container from 'react-bootstrap/Container';
import axios from 'axios';
import Card from 'react-bootstrap/Card';
var qs = require('qs');
const api = axios.create({
  baseURL: 'http://localhost:3002/api/comment/',
  headers: {
    'Content-Type': 'application/x-www-form-urlencoded',
    common: {
      Authorization: localStorage.getItem('token')
    }
  }
});

class DisplayComments extends Component {
  getContentId = async () => {
    const contentId = qs.parse(this.props.match.params, {
      ignoreQueryPrefix: true
    }).id;
    const params = new URLSearchParams();
    params.append('contentId', contentId);

    let data = await api.post('/', params).then(({ data }) => data);
    this.setState({ contentComment: data });
    console.log(this.state);
  };

  constructor(props) {
    super(props);
    this.state = {
      article: { title: null, id: null, content: null }
    };
    this.getContentId();
  }

  render() {
    return (
      <div>
        <Container id='boxContent'>
          <Card style={{ width: '18rem' }}>
            <Card.Body>
              <Card.Title>
                <h2>{this.state.contentComment.contentComment}</h2>
              </Card.Title>
            </Card.Body>
          </Card>
        </Container>
      </div>
    );
  }
}

export default withRouter(DisplayComments);
