import React, { Component } from 'react';
import Container from 'react-bootstrap/Container';
import axios from 'axios';
import Card from 'react-bootstrap/Card';
import { useParams } from 'react-router-dom';
const api = axios.create({
  baseURL: 'http://localhost:3002/api/content/',
  headers: {
    'Content-Type': 'application/x-www-form-urlencoded',
    common: {
      Authorization: localStorage.getItem('token')
    }
  }
});

class DisplayArticle extends Component {
  getContentId = () => {
    function Child() {
      // We can use the `useParams` hook here to access
      // the dynamic pieces of the URL.
      let { id } = useParams();

      return { id };
    }
    Child();
  };
  constructor(props) {
    super(props);
    this.state = {
      article: { title: null, id: null, content: null }
    };

    this.getArticle();
  }

  getArticle = async () => {
    const params = new URLSearchParams();
    params.append('userData', 'urlId');
    let data = api.post('/', params).then(({ data }) => data);
    this.setState({ article: data });
  };
  render() {
    return (
      <div>
        <Container id='boxContent'>
          <Card style={{ width: '18rem' }}>
            <Card.Body>
              <Card.Title>
                <h2>{this.state.article.title}</h2>
              </Card.Title>
              <Card.Subtitle className='mb-2 text-muted'>
                <h2>baw</h2>
              </Card.Subtitle>
            </Card.Body>
          </Card>
        </Container>
      </div>
    );
  }
}

export default DisplayArticle;
