import React, { Component } from 'react';
import { withRouter } from 'react-router';
import Container from 'react-bootstrap/Container';
import axios from 'axios';
import Card from 'react-bootstrap/Card';
const api = axios.create({
  baseURL: 'http://localhost:3002/api/content/',
  headers: {
    'Content-Type': 'application/x-www-form-urlencoded',
    common: {
      Authorization: localStorage.getItem('token')
    }
  }
});

class DisplayArticle extends Component {
  getContentId = () => {
    const { id } = this.props.match.params;

    let data = api.post('/', { id }).then(({ data }) => data);
    this.setState({ article: data });
  };

  constructor(props) {
    super(props);
    this.state = {
      article: { title: null, id: null, content: null }
    };

    this.getContentId();
  }

  render() {
    return (
      <div>
        <Container id='boxContent'>
          <Card style={{ width: '18rem' }}>
            <Card.Body>
              <Card.Title>
                <h2>zrerzrd</h2>
              </Card.Title>
              <Card.Subtitle className='mb-2 text-muted'>
                <h2>baw</h2>
              </Card.Subtitle>
            </Card.Body>
          </Card>
        </Container>
      </div>
    );
  }
}

export default withRouter(DisplayArticle);
