import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/style.css';
import { withRouter } from 'react-router';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import axios from 'axios';

// Création de l'instance axios
const api = axios.create({
  baseURL: 'http://localhost:3002/api/comment/new/',

  headers: {
    'Content-Type': 'application/x-www-form-urlencoded',
    common: {
      Authorization: localStorage.getItem('token')
    }
  }
});

// Fonction permettant de créer un commentaire
const CreateComment = e => {
  const PostComment = () => {
    const params = new URLSearchParams();
    params.append('contentId', contentId);
    params.append('userId', userId);
    params.append('contentComment', contentLog);
    api
      .post('/', params)
      .then(alert('Commentaire ajouté !'))
      .catch(error => {
        console.log(error);
      });
  };
  PostComment();
  return (
    <div>
      <Container id='boxContent'>
        <Form>
          <Form.Group className='mb-3' controlId='formBasicContent'>
            <Form.Label>Commentaires</Form.Label>
            <Form.Control
              type='text'
              onChange={e => {
                setContentLog(e.target.value);
              }}
              placeholder='Contenu de votre article'
            />
          </Form.Group>

          <Button onClick={CreateComment} variant='primary' type='submit'>
            Ajouter le commentaire
          </Button>
        </Form>
      </Container>
    </div>
  );
};

export default withRouter(CreateComment);
