import { useState } from 'react';
import { Redirect } from 'react-router-dom';
import axios from 'axios';
// Création de l'instance axios
const api = axios.create({
  baseURL: 'http://localhost:3002/api/users/me',
  headers: {
    'Content-Type': 'application/x-www-form-urlencoded',
    common: {
      Authorization: localStorage.getItem('token')
    }
  }
});

const IsNotConnected = () => {
  let [isNotAuth, setIsNotAuth] = useState('');

  const isUserNotAuth = async () => {
    let data = localStorage.getItem('userId');
    if (data) {
      setIsNotAuth(false);
    } else {
      setIsNotAuth(true);
    }
    if (setIsNotAuth === true) {
      return true;
    } else {
      return false;
    }
  };

  return <div>{isUserNotAuth() === true && <Redirect to='/articles' />}</div>;
};

export default IsNotConnected;
