import { useState } from 'react';
import { Redirect } from 'react-router-dom';
import axios from 'axios';
// Création de l'instance axios
const api = axios.create({
  baseURL: 'http://localhost:3002/api/users/me',
  headers: {
    'Content-Type': 'application/x-www-form-urlencoded',
    common: {
      Authorization: localStorage.getItem('token')
    }
  }
});

const IsNotConnected = () => {
  let [isNotAuth, setIsNotAuth] = useState('');
  const params2 = new URLSearchParams();
  params2.append('userData', localStorage.getItem('userId'));

  const getUser = async () => {
    const data = await api.post('/', params2).then(({ data }) => data);
    console.log(data);
    if (data.id === localStorage.getItem('userId')) {
      setIsNotAuth(true);
    } else {
      setIsNotAuth(false);
    }
  };
  getUser();
  const isUserAuth = () => {
    if (isNotAuth === true) {
      return true;
    } else {
      return true;
    }
  };

  return <div>{isUserAuth() === false && <Redirect to='/' />}</div>;
};

export default IsNotConnected;
