import React, { Component } from 'react';
import Container from 'react-bootstrap/Container';
import axios from 'axios';
import Card from 'react-bootstrap/Card';
var qs = require('qs');
import { useParams } from 'react-router';

const api = axios.create({
  baseURL: 'http://localhost:3002/api/content/',
  headers: {
    'Content-Type': 'application/x-www-form-urlencoded',
    common: {
      Authorization: localStorage.getItem('token')
    }
  }
});
function GetContentId() {
  let { id } = useParams();
  console.log({ id });
  return { id };
}
class DisplayArticle extends Component {
  constructor(props) {
    super(props);
    this.state = {
      article: { title: null, id: null, content: null }
    };
    GetContentId();
    this.getArticle();
  }

  getArticle = async () => {
    GetContentId();
    const params = new URLSearchParams();
    let contentId;
    params.append('userData', contentId);
    let data = await api.post('/', params).then(({ data }) => data);

    this.setState({ article: data });
  };
  render() {
    return (
      <div>
        <Container id='boxContent'>
          <Card style={{ width: '18rem' }}>
            <Card.Body>
              <Card.Title>
                <h2>baw</h2>
              </Card.Title>
              <Card.Subtitle className='mb-2 text-muted'>
                <h2>baw</h2>
              </Card.Subtitle>
            </Card.Body>
          </Card>
        </Container>
      </div>
    );
  }
}

export default DisplayArticle;
