import { useState } from 'react';
import { Redirect } from 'react-router-dom';
import axios from 'axios';
// Création de l'instance axios
const api = axios.create({
  baseURL: 'http://localhost:3002/api/users/me',
  headers: {
    'Content-Type': 'application/x-www-form-urlencoded',
    common: {
      Authorization: localStorage.getItem('token')
    }
  }
});

const IsConnected = () => {
  let [isAuth, setIsAuth] = useState('');
  const params = new URLSearchParams();
  params.append('userData', localStorage.getItem('userId'));
  const getUser = async () => {
    const data = await api.post('/', params).then(({ data }) => data);
    console.log(data);
    if (data) {
      setIsAuth(true);
    } else {
      setIsAuth(false);
    }
  };
  getUser();
  const isUserAuth = () => {
    if (isAuth === true) {
      return true;
    } else {
      return false;
    }
  };

  return <div>{isUserAuth() === false && <Redirect to='/' />}</div>;
};

export default IsConnected;
